import { Formik , Form } from "formik";
import React, { Component } from "react";
import CustomInput from "./custom-input";
import { yupLoginSchema } from "../schemas/loginForm-schema";

const RegisterForm = () => {
  return (<Formik initialValues={{
    username : "",
    password : "",
  }}
  enableReinitialize
  validationSchema={yupLoginSchema}
  >
    {(props) => (
        <div className="login-card d-flex align-items-center justify-content-center">
            <Form className="inner-login-card d-flex flex-column">
                <CustomInput labelClass={"fw-bold "} name={"username"} value={props.values.username} id={"usename"} type="text"/>

                <CustomInput labelClass={"fw-bold"} name={"password"} value={props.values.password} id={"password"} type="text"/>

                <button className="btn btn-dark mt-5" type="submit">Log in</button>
            </Form>
        </div>
    )}
  </Formik>);
};

export default RegisterForm;
