import React, { Component } from "react";

class Counter extends Component {
  componentWillUnmount() {
    console.log("component unmounted!");
    // some clean up after deleting the component
  }

  render() {
    console.log("Counter renderd!");

    return (
        <div className="continer w-50">
          {this.props.id}
          <div className="row w-25">
            <div className="col ">
              <span className={this.changeBadgeColor()}>
                {this.formatNumber()}
              </span>
            </div>

            <div className="col">
              <button
                onClick={() => this.props.onIncrement(this.props.counter)}
                className="btn btn-secondary fs-5 m-2"
              >
                +
              </button>
              <button className="btn btn-secondary m-2 fs-5">-</button>
              <button
                onClick={this.props.onDelete}
                className="btn btn-danger m-2 fs-5"
              >
                x
              </button>
            </div>
          </div>
        </div>
    );
  }

  changeBadgeColor() {
    let classes = "badge m-2 fs-6 bg-";
    const { value } = this.props.counter;
    return (classes += value === 0 ? "warning" : "primary");
  }

  formatNumber() {
    const { value } = this.props.counter;
    return value === 0 ? "Zero" : value;
  }

  // renderTags() {
  //   if (this.state.tags.length === 0) return <p>There are no tags!</p>;

  //   return (
  //     <ul>
  //       {this.state.tags.map((tag) => (
  //         <li key={tag}>{tag}</li>
  //       ))}
  //     </ul>
  //   );
  // }
}

export default Counter;
