import React, { Component } from "react";

class Counter extends Component {
  state = {
    count : 0,
    tags : ["tag1" , "tag2" , "tag3"],
  };

  increaseNumber() {
    const {count} = this.state;
    count++;
  }

  render() {


    return (
      <React.Fragment>
        <div>
          <span className={this.changeBadgeColor()}>{this.formatNumber()}</span>
          <button onClick={increaseNumber()} className="btn btn-secondary">increment</button>
          {this.renderTags()}
        </div>
      </React.Fragment>
    );
  }


  changeBadgeColor() {
    let classes = "badge m-2 fs-6 bg-";
    const { count } = this.state;
    return classes += count === 0 ? "warning" : "primary";
  }

  formatNumber() {
    const {count} = this.state;
    return count === 0 ? 'Zero' : count;
  }


  renderTags() {
    if (this.state.tags.length === 0) 
      return <p>There are no tags!</p>

      return <ul>{this.state.tags.map(tag => <li key={tag} >{tag}</li>)}</ul> 
  }
}

export default Counter;
