import React, { Component } from "react";

class Counter extends Component {


  render() {

    return (
      <React.Fragment>
        <div>
            {this.props.id}
            <span className={this.changeBadgeColor()}>{this.formatNumber()}</span>
            <button onClick={ () => this.props.onIncrement(this.props.counter)} className="btn btn-secondary">
              increment
            </button>
            <button onClick={this.props.onDelete} className="btn btn-danger m-2 btn-sm">Delete</button>
        </div>
      </React.Fragment>
    );
  }

  changeBadgeColor() {
    let classes = "badge m-2 fs-6 bg-";
    const { value } = this.state;
    return (classes += value === 0 ? "warning" : "primary");
  }

  formatNumber() {
    const { value } = this.state;
    return value === 0 ? "Zero" : value;
  }

