import { getAllByDisplayValue } from "@testing-library/react";
import React, { Component } from "react";
import Counter from "./counter";

class Counters extends Component {
  state = {
    counters : [
        {id:1 , value:4},
        {id:2 , value:0},
        {id:3 , value:0},
        {id:4 , value:0}
    ]
  };

  handleDelete(counterToRemove) {
    const counters = this.state.counters.filter(counter => counter.id  ) 
  }

  render() {
    return (
      <div>
        {this.state.counters.map(counter => <Counter key={counter.id} onDelete={() => this.handleDelete(counter)} value={counter.value} selected id={counter.id} />
        )}
      </div>
    );
  }
}

export default Counters;
