import { getAllByDisplayValue } from "@testing-library/react";
import React, { Component } from "react";
import { findRenderedDOMComponentWithClass } from "react-dom/test-utils";
import Counter from "./counter";

class Counters extends Component {
  state = {
    counters: [
      { id: 1, value: 4 },
      { id: 2, value: 0 },
      { id: 3, value: 0 },
      { id: 4, value: 0 },
    ],
  };

  handleIncrement = (counter) => {
    const counters = { ...this.state.counters };
    const index = counters.indexOf(counter);
    counters[index] = { ...counter };
    counters[index].value++;
    this.setState({ counters });
  };

  handleDelete(counterToRemove) {
    const counters = this.state.counters.filter(
      (counter) => counter.id !== counterToRemove.id
    );
    this.setState({ counters });
  }

  handleReset = () => {
    const counters = this.state.counters.map((counter) => {
      counter.value = 0;
      return counter;
    });
    this.setState({ counters });
  };

  render() {
    return (
      <div>
        <button onClick={this.handleReset} className="btn btn-sm btn-info m-3">
          Reset All
        </button>
        {this.state.counters.map((counter) => (
          <Counter
            key={counter.id}
            onDelete={() => this.handleDelete(counter)}
            onIncrement={() => this.handleIncrement(counter)}
            counter={counter}
          />
        ))}
      </div>
    );
  }
}

export default Counters;
